                    if (nn2[0] == "@var_retain" || nn2[0] == "@var_copy") vpos = 1;
                    if (nn2.indexOf("@end_vars") == -1) nn2.splice(vpos, 0, "@end_vars");
                    vpos = nn2.indexOf("@end_vars");
                    nn2.splice(vpos + 1, 0, total);
